const SEND_MESSAGE = 'SEND-MESSAGE';

let store = {
    _state: {
        profilePage: {
            postsData: [
                {id: 1, post: 'How are you?', likeCount: '5'},
                {id: 2, post: 'It\'s my first test!', likeCount: '0'},
                {id: 3, post: 'All right?', likeCount: '777'},
                {id: 4, post: 'What are you doing?', likeCount: '1000'}
            ],
            newPostText: 'NikArt Yohhoo!'
        },
        messagePage: {
            dialogData: [
                {id: 1, name: 'NikArt'},
                {id: 2, name: 'Andrey'},
                {id: 3, name: 'Anya'},
                {id: 4, name: 'Mama'}
            ],
            messageData: [
                {id: 1, message: 'Hi???'},
                {id: 2, message: 'Good!'},
                {id: 3, message: 'All right?'},
                {id: 4, message: 'Welcome !!!'}
            ],
            newMessageBody: 'NNNNNNNN'
        }
    },
    _callSubscriber(){

    },

    getState() {
        return this._state;
    },

    subscribe(observer) {
        this._callSubscriber = observer;
    },

    dispatch(action) {
        profileReducer(this._state.profilePage);

        if (action.type === ADD_POST) {
            // debugger;
            let newPost = {
                id: 5,
                post: this._state.profilePage.newPostText,
                likeCount: '0'
            };
            this._state.profilePage.postsData.push(newPost);
            this._state.profilePage.newPostText = '';
            this._callSubscriber(this._state);
        } else if (action.type === UPDATE_NEW_POST_TEXT) {
            this._state.profilePage.newPostText = action.newText;
            this._callSubscriber(this._state);
        } else if (action.type === UPDATE_NEW_MESSAGE_BODY) {
            this._state.messagePage.newMessageBody = action.body;
            this._callSubscriber(this._state);
        } else if (action.type === SEND_MESSAGE) {
            let body = this._state.messagePage.newMessageBody;
            this._state.messagePage.newMessageBody = '';
            this._state.messagePage.messageData.push({id: 6, message: body});
            this._callSubscriber(this._state);


        }
    }
}
export const addPostActionCreator = () => ({type: ADD_POST});
export const updateNewPostTextActionCreator = (text) => ({type: UPDATE_NEW_POST_TEXT, newText: text});

export const sendMessageCreator = () => ({type: SEND_MESSAGE});
