import s from './Messages.module.css';
import {NavLink} from "react-router-dom";

const DialogItem = (props) => {
    let path ='/dialogs/' + props.id;

    return (
        <div className={s.dialog + ' ' + s.active}>
            <NavLink to={path}>{props.name}</NavLink>
        </div>
    )
}

const Mess = (props) => {
    return (
        <div className={s.message}>{props.message}</div>
    )
}

const Messages = (props) => {
    let dialogData = [
        {id: 1, name: 'NikArt'},
        {id: 2, name: 'Andrey'},
        {id: 3, name: 'Anya'},
        {id: 4, name: 'Mama'}
    ]

    let messageData = [
        {id: 1, message: 'Hi???'},
        {id: 2, message: 'Good!'},
        {id: 3, message: 'All right?'},
        {id: 4, message: 'Welcome !!!'}
    ]


    // let dialogsElements = [
    //     <DialogItem name={dialogData[0].name} id={dialogData[0].id} />,
    //     <DialogItem name={dialogData[1].name} id={dialogData[1].id} />,
    //     <DialogItem name={dialogData[2].name} id={dialogData[2].id} />,
    //     <DialogItem name={dialogData[3].name} id={dialogData[3].id} />
    // ]
    let dialogsElements = dialogData.map(dialog => <DialogItem name={dialog.name} id={dialog.id} />);
    let messageElements = messageData.map(mess => <Mess message={mess.message} />);


    return (
        <div className={s.dialogs}>

            <div className={s.dialogsItem}>
                {dialogsElements}
            {/*    <DialogItem name={dialogData[0].name} id={dialogData[0].id} />*/}
            {/*    <DialogItem name={dialogData[1].name} id={dialogData[1].id} />*/}
            {/*    <DialogItem name={dialogData[2].name} id={dialogData[2].id} />*/}
            {/*    <DialogItem name={dialogData[3].name} id={dialogData[3].id} />*/}
            </div>
            <div className={s.messages}>
                {messageElements}
                {/*<Mess message={messageData[0].message} />*/}
                {/*<Mess message='Good!' />*/}
                {/*<Mess message='All right' />*/}
                {/*<Mess message='Welcome!' />*/}
            </div>
        </div>
    )
}

export default Messages;
